public function getNewSortOrder() {
    $sql = "SELECT sort_order FROM `" . DB_PREFIX . "modification_order` ORDER BY sort_order DESC LIMIT 1";
    $result = $this->db->query($sql);
    if ($result->num_rows > 0) {
        return (int)$result->row['sort_order'] + 1;
    }
    return null;
}

